mapper.insert:


SELECT NVL(MAX(ATT_NO), 0) + 1
FROM ATTATCH

INSERT ALL


   INTO ATTATCH(
   		BO_NO, ATT_NO
   		, ATT_FILENAME
   		, ATT_SAVENAME
   		, ATT_SIZE
   		, ATT_CONTENTTYPE
   	)
   VALUES(
   	#{bo_no}, #{startAttNo}+#{idx}
   	, #{attatch.att_filename, jdbcType=VARCHAR}
   	, #{attatch.att_savename, jdbcType=VARCHAR}
   	, #{attatch.att_size, jdbcType=NUMERIC}
   	, #{attatch.att_contenttype, jdbcType=VARCHAR}

   	)


SELECT * FROM DUAL

mapper.delete:
DELETE FROM ATTATCH
WHERE ATT_NO IN 

   #{delNo}
